SELECT * FROM (SELECT ROWNUM rn, a.* FROM (SELECT * FROM project WHERE p_state = '03' and
period >= #{start} ORDER BY p_regdt DESC) a WHERE ROWNUM <= #{page} * #{pageSize}) WHERE rn BETWEEN (#{page}-1)* #{pageSize} + 1 AND #{page} * #{pageSize}
period between #{start} and #{end} ORDER BY p_regdt DESC) a WHERE ROWNUM <= #{page} * #{pageSize}) WHERE rn BETWEEN (#{page}-1)* #{pageSize} + 1 AND #{page} * #{pageSize}
ORDER BY p_regdt DESC) a WHERE ROWNUM <= #{page} * #{pageSize}) WHERE rn BETWEEN (#{page}-1)* #{pageSize} + 1 AND #{page} * #{pageSize}

SELECT * FROM (SELECT ROWNUM rn, a.* FROM (SELECT * FROM project WHERE p_state = '03' and
p_money >= #{start} ORDER BY p_regdt DESC) a WHERE ROWNUM <= #{page} * #{pageSize}) WHERE rn BETWEEN (#{page}-1)* #{pageSize} + 1 AND #{page} * #{pageSize}
p_money between #{start} and #{end} ORDER BY p_regdt DESC) a WHERE ROWNUM <= #{page} * #{pageSize}) WHERE rn BETWEEN (#{page}-1)* #{pageSize} + 1 AND #{page} * #{pageSize}
ORDER BY p_regdt DESC) a WHERE ROWNUM <= #{page} * #{pageSize}) WHERE rn BETWEEN (#{page}-1)* #{pageSize} + 1 AND #{page} * #{pageSize}

SELECT * FROM (SELECT ROWNUM rn, a.* FROM (select p_title, user_id, p_money, period, p_regdt, deadline, p_code from project where p_state = '03'
and p_field = #{value1}
and p_field IN (#{value1},#{value2})
and p_field IN (#{value1},#{value2},#{value3})
and p_field IN (#{value1},#{value2},#{value3},#{value4})
ORDER BY user_id) a WHERE ROWNUM <= #{page} * #{pageSize}) WHERE rn BETWEEN (#{page} - 1)* #{pageSize} + 1 AND #{page} * #{pageSize}
